import React from 'react';
import { Link } from 'react-router-dom';
import { useNavigate , useLocation , useParams} from 'react-router-dom';
import axios from "axios";
import { useState } from 'react';
import { useEffect } from 'react';
import { response } from 'express';


function BoardDetail() {
   
    let location = useLocation();
    var [post, setPost] = useState([]); // 게시글 정보를 저장할 상태 변수
    let [pcount , tcount] = useState(0);
    const { id } = useParams();
    const [totalDetail, setTotalDetail] = useState('');

    useEffect(() => {
      axios.get(`http://localhost:4000/Board/${id}`)
        .then((response) => {
        setTotalDetail(response.data); // 서버에서 받은 데이터를 상태 변수에 저장
        console.log(totalDetail);
          // let total = response.data;
          // console.log(total);
        })
        .catch((error)=>{
          console.error('Detail 가져오다가 오류 발생:', error);
        });

        return(
          {totalDetail}
        )

      }, []); 

    

      return (
        <div>
        <h2>게시글 상세정보</h2> <br/>
        
        <div className="post-view-row">
        <label>게시글 번호</label>
        <label>{ post.no}</label>
        {/* <label>{total[0].BOARD_NO}</label> */}
        </div>
        <div className="post-view-row">
        <label>제목</label>
        <label>{ post.title }</label>
        {/* <label>{total[0].BOARD_TITLE}</label> */}
        </div>
        <div className="post-view-row">
        <label>작성자</label>
        <label>{ post.who }</label>
        </div>
        <div className="post-view-row">
        <label>조회수</label>
        <label>{ pcount }</label>
        </div>
       
        <div >
        <td/>
         <div className='content'>
       {
        post.content
         }
         </div>

        </div>
        </div>
      );

 }

/* <div className="post-view-row">
<label>첨부파일</label>
{image && <img src={`data:image/jpeg;base64,${image}`} alt="이미지" />}
</div> */

export default BoardDetail;
