import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Link } from 'react-router-dom';

function GetCategory() {
  const [category, setCategory] = useState({});

  useEffect(() => {
    axios.get('http://localhost:4000/').then((response)=> {
      setCategory(response.data);
    })
  }, []);

  const categories = (Object.values(category)).map((item) => (
    <option key={item.id} value={item.id}>
      {item.displayName}
    </option>
  ));

  return categories;
}

const HandleQuestionSubmit = async({body}) => {
  const headers = {
    'Content-Type' : 'application/json',
    'Authorization' : "Bearer cognito 의 access token"
  }

const response = await axios.post('http://localhost:4000', body ).then((response) => {
    console.log('status : '+response.status);
  }).catch((error) => {
    console.log('error : '+error);
  });
}

function Board() {


  // 게시글 제목, 글쓴이, 내용, 그리고 선택한 파일을 관리할 상태 변수
  const [title, setTitle] = useState('');
  const [who, setWho] = useState('');
  const [content, setContent] = useState('');
  const [file, setFile] = useState('');
  let navigate = useNavigate();
  const categories = GetCategory();

  // 게시글을 제출하는 함수
  const Submit = () => {
 

      const body = {
      title: title,
      who : who,
      content: content
    }


  //  const newPost = [
  //     title,
  //     who,
  //     content,
  //  ];
    // console.log(newPost);
    //addPost(newPost);
   
    navigate('/List',  {
      state : {
      title ,
      who
    }
  });


  const [category, setCategory] = useState({});

  useEffect(() => {
    axios.get('http://localhost:4000/').then((response)=> {
      setCategory(response.data);
    })
  }, []);
 
  

    // 게시글 작성 후 입력 내용 초기화
    setTitle('');
    setWho('');
    setContent('');
    setFile('');
  };

 
  
  return (
    
    <div className='bar'>
      <h1>글 작성</h1>
      <form>
        <div>
          <div htmlFor="title">제목</div>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className='space'
          />
        </div>
        <div>
          <div htmlFor="who">글쓴이</div>
          <input
            type="text"
            id="who"
            value={who}
            onChange={(e) => setWho(e.target.value)}
            className='space'
          />
        </div>
        <div>
          <div htmlFor="content">내용</div>
          <textarea
            id="content"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            className='space1'
          />
        </div>
        <div>
          <input
            type="file"
            id="file"
            accept="image/*" // 이미지 파일만 허용하도록 설정
            value={file}
            onChange={(e) => setFile(e.target.value)}
            className='space'
          />
        </div>
        <button type="button" onClick={Submit}>
          게시글 작성
        </button>
      </form>
      
    </div>
  );
}



export default Board;
