import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Board = () => {
  const navigate = useNavigate();

  const [write, setWrite] = useState({
    title: '',
    createdBy: '',
    contents: '',
  });

  const { title, createdBy, contents } = write; //비구조화 할당

  const onChange = (event) => {
    const { value, name } = event.target; //event.target에서 name과 value만 가져오기
    setWrite({
      ...write,
      [name]: value,
    });
  };

  const saveWrite = async () => {
    await axios.post(`//localhost:8080/write`, write).then((res) => {
      alert('등록되었습니다.');
      navigate('/write');
    });
  };

  const backToList = () => {
    navigate('/write');
  };

  return (
    <div>
      <div>
        <span>제목</span>
        <input type="text" name="title" value={title} onChange={onChange} />
      </div>
      <br />
      <div>
        <span>작성자</span>
        <input
          type="text"
          name="createdBy"
          value={createdBy}
          onChange={onChange}
        />
      </div>
      <br />
      <div>
        <span>내용</span>
        <textarea
          name="contents"
          cols="30"
          rows="10"
          value={contents}
          onChange={onChange}
        ></textarea>
      </div>
      <br />
      <div>
        <button onClick={saveWrite}>저장</button>
        <button onClick={backToList}>취소</button>
      </div>
    </div>
  );
};

export default Board;
