import React, { useState , useEffect} from 'react';
import {arr} from './Main';

function Result (value)  {
  // 결과를 계산할 때 사용할 상태 변수
  console.log(arr);
  let [score, totalScore] = useState(0);

  useEffect(()=>{
    const arr = {arr};
  }, [{arr}]);


  const spec = [4, 5, 7, 8];

    if (spec.includes(arr)) {
      switch (value) {
        case '전혀':
          score = 3;
          break;
        case '가끔':
          score = 2;
          break;
        case '자주':
          score = 1;
          break;
        case '항상':
          score = 0;
          break;
        default:
          score = 0;
      }
    } else {
      switch (value) {
        case '전혀':
          score = 0;
          break;
        case '가끔':
          score = 1;
          break;
        case '자주':
          score = 2;
          break;
        case '항상':
          score = 3;
          break;
        default:
          score = 0;
      }
    }
    
    totalScore += score;
    
    
    // <div>
    //   <h1>결과</h1>
    //   <div> 총 점수 : {totalScore}</div>
    // </div>
    
    console.log(Result.totalScore);
  }
  
export default Result;
