import React, { useState , useEffect} from 'react';
import Main from './Main';
import {useLocation} from 'react-router-dom';

function Result (props)  {

  const location = useLocation();

  const answer = location.state;
  console.log(answer);

 //결과를 계산할 때 사용할 상태 변수
  let [totalscore, setTotalScore] = useState(0);
  
  useEffect(()=>{
    const spec = [4, 5, 7, 8];

    let score = 0;

    if (spec.includes(answer)) {
      switch (answer.value) {
        case '전혀':
          score = 3;
          break;
        case '가끔':
          score = 2;
          break;
        case '자주':
          score = 1;
          break;
        case '항상':
          score = 0;
          break;
        default:
          score = 0;
      }
    } else {
      switch (answer.value) {
        case '전혀':
          score = 0;
          break;
        case '가끔':
          score = 1;
          break;
        case '자주':
          score = 2;
          break;
        case '항상':
          score = 3;
          break;
        default:
          score = 0;
      }

      setTotalScore(totalscore + score);

    }
    

  },[answer]);
 
    
    
    
    
    console.log(setTotalScore);
  }
  
export default Result;
