import React from 'react';
import { Link } from 'react-router-dom';
import { useNavigate , useLocation , useParams} from 'react-router-dom';
import axios from "axios";
import { useState } from 'react';
import { useEffect } from 'react';
import Board from './Board';

function BoardDetail() {
   
    let location = useLocation();
    const [post, setPost] = useState(null); // 게시글 정보를 저장할 상태 변수
    const { newPost } = location.state;
    const {postId } = location.state;
    const [detail, setDetail] = useState('');
    let [pcount , tcount] = useState(0);
    const { id } = useParams();

    useEffect(() => {
      axios.get(`http://localhost:4000/BoardDetail/${id}`)
        .then((response) => {
        setPost(response.data); // 서버에서 받은 데이터를 상태 변수에 저장
        console.log(setPost);
        })
        .catch((error) => {
          console.error('이미지 가져오기 중 오류 발생:', error);
        });
      }, []); 


      return (
        <div>
          <h2>게시글 상세정보</h2> <br/>
    
          {/* 게시글 정보를 화면에 출력 */}
          {post ? (
            <div>
              <div className="post-view-row">
                <label>게시글 번호</label>
                <label>{post.no}</label>
              </div>
              <div className="post-view-row">
                <label>제목</label>
                <label>{post.title}</label>
              </div>
              <div className="post-view-row">
                <label>작성자</label>
                <label>{post.who}</label>
              </div>
              <div className="post-view-row">
                <label>조회수</label>
                <label>{post.pcount}</label>
              </div>
              <div>
                <td/>
                <div className='content'>
                  {post.content}
                </div>
              </div>
            </div>
          ) : (
            // 게시글 정보를 아직 불러오지 못한 경우 로딩 메시지나 오류 메시지 출력 가능
            <p>로딩 중...</p>
          )}
        </div>
      );
    }
    

/* <div className="post-view-row">
<label>첨부파일</label>
{image && <img src={`data:image/jpeg;base64,${image}`} alt="이미지" />}
</div> */

export default BoardDetail;
