import React, { useState, useEffect } from 'react';
import {useLocation} from 'react-router-dom';
import axios from "axios";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import Board from './Board';
import {Link} from "react-router-dom";

  function List(props) {
    const [data, setData] = useState([]);
    useEffect(() => {
      axios.get('http://localhost:4000/Board')
      .then((response) => {
        setData(response.data); // 서버에서 받은 데이터를 상태 변수에 저장
        console.log(response.data);
      })
      .catch((error) => {
        console.error('데이터 가져오기 중 오류 발생:', error);
      });
    }, []); 

   

  return (
    <div>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>번호</th>
            <th>제목</th>
            <th>내용</th>
            <th>작성자</th>
          </tr>
        </thead>
        <tbody>
    
      {
      Object.keys(data).map((a, i) => (
      <tr key={i}>
      <td>{i +1 }</td>
      <td>{data[a].BOARD_TITLE}</td>
      <td>{data[a].BOARD_CONTENT}</td>
      <td>{data[a].BOARD_NAME}</td>
      </tr>
      ))}

          
        {/* {boardData.map((board)=> (
          <tr key={board.idx}>
              <td>{boardData.idx}</td>
              <td>{boardData.title}</td>
              <td>{boardData.content}</td>
              <td>{boardData.who}</td>
            </tr>
          ))} */}

        </tbody>
      </Table>
      <Button variant="info">글쓰기</Button>
      <Button variant="secondary">수정하기</Button>
      <Button variant="danger">삭제하기</Button>
    </div>
  );
}

export default List;
