import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Link } from 'react-router-dom';

function Board() {


  // 게시글 제목, 글쓴이, 내용, 그리고 선택한 파일을 관리할 상태 변수
  const [boardList, setBoardList] = useState([]);
  const [title, setTitle] = useState('');
  const [who, setWho] = useState('');
  const [content, setContent] = useState('');
  const [file, setFile] = useState('');
  let navigate = useNavigate();
  
  // 게시글을 제출하는 함수
  const Submit = () => {

   const newPost = [
      title,
      who,
      content,
   ];
    
   
    navigate('/List',  {
      state : {
      title ,
      who,
      content
    }
  });

  // axios.post("http://localhost:4000/Board", newPost)
  // .then((response) => {
  //   console.log(response.data); // 서버에서 받은 응답 데이터를 확인합니다.
  //   alert("등록 완료!");
  // })
  // .catch((error) => {
  //   console.error("등록 중 오류 발생:", error);
  // });

  axios.post("/Board" ,(req,res)=>{
    const {title, who, content} = req.body;
    var sqlQuery = `INSERT INTO board (BOARD_TITLE, BOARD_NAME , BOARD_CONTENT) VALUES ('${title}', '${who}', '${content}')`;
    DB.query(sqlQuery, (err, result) => {
    res.send('Success')
    if (err) throw err;
    console.log("insert success");
  }
)
});

  axios.get('http://localhost:4000/Board') // GET 요청으로 서버에서 데이터를 가져옵니다.
  .then((response) => {
    setBoardList(response.data); // 서버에서 받은 데이터를 상태 변수에 저장합니다.
    console.log(setBoardList);
  })
  .catch((error) => {
    console.error('데이터 가져오기 중 오류 발생:', error);
  });


    // 게시글 작성 후 입력 내용 초기화
    setTitle('');
    setWho('');
    setContent('');
    setFile('');
  };

 
  return (
    
    <div className='bar'>
      <h1>글 작성</h1>
      <form>
        <div>
          <div htmlFor="title">제목</div>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className='space'
          />
        </div>
        <div>
          <div htmlFor="who">글쓴이</div>
          <input
            type="text"
            id="who"
            value={who}
            onChange={(e) => setWho(e.target.value)}
            className='space'
          />
        </div>
        <div>
          <div htmlFor="content">내용</div>
          <textarea
            id="content"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            className='space1'
          />
        </div>
        <div>
          <input
            type="file"
            id="file"
            accept="image/*" // 이미지 파일만 허용하도록 설정
            value={file}
            onChange={(e) => setFile(e.target.value)}
            className='space'
          />
        </div>
        <button type="button" onClick={Submit}>
          게시글 작성
        </button>
      </form>
      
    </div>
  );
}

export default Board;
