import React, { useState , useEffect} from 'react';
import {useLocation} from 'react-router-dom';

function Result ()  {

  const location = useLocation();
  const newArray = location.state;

//  //결과를 계산할 때 사용할 상태 변수
  let [totalscore, setTotalScore] = useState(0);
  
  useEffect(()=>{
      const newData = newArray.newArray;
      
      let score1 = 0;
      let score2 = 0;
      newData.forEach((item)=>{
        const [questionN, value] = item;
        
        const spec = [4, 5, 7, 8];
        if (spec.includes(Number(questionN))) {
        switch (value) {
          case '전혀':
            score1 += 3;
            break;
          case '가끔':
            score1 += 2;
            break;
          case '자주':
            score1 += 1;
            break;
          case '항상':
            score1 += 0;
            break;
          default:
            score1 += 0;
        }
       }else {
        switch (value) {
              case '전혀':
                score2 += 0;
                break;
              case '가끔':
                score2 += 1;
                break;
              case '자주':
                score2 += 2;
                break;
              case '항상':
                score2 += 3;
                break;
              default:
                score2 += 0;
            }
       }});
      let totalscore = score1 + score2;
      setTotalScore(totalscore);
      // }
    }, [newArray.newArray]);
      


    // useEffect(() => {
    //   // totalscore 상태 값 변경 후에 로그 출력
    // }, [totalscore]);
     
    return (
      <div>
        <div>총점수</div>
        <div>{totalscore}</div>
      </div>
    )

  }
  

export default Result;
