import React, { useState } from 'react';
import axios from 'axios';
import Board from './Board';

function List({post}) {

    const [posts, setPosts] = useState([]); // 게시글 목록을 관리할 상태 변수

  // 게시글을 추가하는 함수
  const addPost = (newPost) => {
    setPosts([...posts, newPost]);
  };
  return (
    <div>
      <h2>게시글</h2>
      <ul>
        {post.map((item, index) => (
          <li key={index}>
            <strong>제목:</strong> {item.title}, <strong>글쓴이:</strong> {item.who}
          </li>
        ))}
      </ul>
      <Board addPost={addPost} />
    </div>
  );
}

export default List;
