export function calculateScore(setAnswer) {
  const spec = [4, 5, 7, 8];

  // setAnswer를 배열로 변환
  const setAnswerArray = Object.values(setAnswer);

  // 각 요소에 대한 점수를 계산하고 새로운 배열에 저장
  const scores = setAnswerArray.map((value, index) => {
      let score = 0;
      const questionId = index + 1;

      if (spec.includes(questionId)) {
          switch (value) {
              case '전혀':
                  score = 3;
                  break;
              case '가끔':
                  score = 2;
                  break;
              case '자주':
                  score = 1;
                  break;
              case '항상':
                  score = 0;
                  break;
              default:
                  score = 0;
          }
      } else {
          switch (value) {
              case '전혀':
                  score = 0;
                  break;
              case '가끔':
                  score = 1;
                  break;
              case '자주':
                  score = 2;
                  break;
              case '항상':
                  score = 3;
                  break;
              default:
                  score = 0;
          }
      }

      return score;
  });

  // scores 배열을 반환
  return scores;
}
