import React, { useState, useEffect } from 'react';
import {useLocation} from 'react-router-dom';
import axios from "axios";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import Board from './Board';


  function List(props) {

    const [boardList, setBoardList] = useState([]);

      // axios.get('http://localhost:4000/Board') // GET 요청으로 서버에서 데이터를 가져옵니다.
      //   .then((response) => {
      //     setBoardList(response.data); // 서버에서 받은 데이터를 상태 변수에 저장합니다.
      //     console.log(setBoardList);
      //   })
      //   .catch((error) => {
      //     console.error('데이터 가져오기 중 오류 발생:', error);
      //   });

    
  console.log(boardList);

  // useEffect(() => {
    // 서버에서 데이터를 가져오는 GET 요청
    axios.get('http://localhost:4000/Board')
      .then((response) => {
        setBoardList(response.data); // 서버에서 받은 데이터를 상태 변수에 저장
      })
      .catch((error) => {
        console.error('데이터 가져오기 중 오류 발생:', error);
      });
  // }, []); // useEffect를 빈 배열로 전달하여 컴포넌트가 처음 렌더링될 때만 실행
  
  
  return (
    <div>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>번호</th>
            <th>제목</th>
            <th>작성자</th>
            <th>내용</th>
          </tr>
        </thead>
        <tbody>

        {
        boardList.map((a, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{a.title}</td>
              <td>{a.who}</td>
              <td>{a.content}</td>
            </tr>
          ))}

        </tbody>
      </Table>
      <Button variant="info">글쓰기</Button>
      <Button variant="secondary">수정하기</Button>
      <Button variant="danger">삭제하기</Button>
    </div>
  );
}


export default List;
