import {  useState } from "react";
import Form from 'react-bootstrap/Form';
import data from "../data.js";
import { Routes, Route, Link, navigate } from 'react-router-dom';
import Result from "./Result.js";

function Main() {
  let [answer, setAnswer] = useState({});
  let [num, setNum] = useState([]);
  let [page, setPage] = useState(1);
  let totalPage = 5; // 한 페이지에 표시할 질문의 수를 나타내는 변수
  
  let start = (page - 1) * totalPage;
  let end = start + totalPage;
  let curPage = data.slice(start, end);
  const isLastPage = end >= data.length;
  const isFristPage = start != 0 ;
  //let navigate = useNavigate();

  

  const nextPage = () => {
    let ques = data
      .slice(start, end)
      .filter((test) => !answer[`question_${test.id}`]);

    if (ques.length > 0) {
      const num = ques.map((question) => question.id);
      setNum(num);
      alert(`${num.join(", ")} 번 질문에 답변해주세요!`);
    } else {
      if (isLastPage) {
        navigate('/result');



        
      } else {
        setPage(page + 1);
      }
    }
  };

  const change = (questionId, value) => {
    setAnswer({
      ...answer,
      [questionId]: value
    });
  };

  const totalScore = (questionId, value) => {
    // 문항 4, 5, 7, 8에 대한 규칙
    const spec = [4, 5, 7, 8];

    // 선택한 값에 따라 점수를 설정
    let score;
    if (spec.includes(questionId)) {
      switch (value) {
        case '아니다':
          score = 3;
          break;
        case '가끔':
          score = 2;
          break;
        case '자주':
          score = 1;
          break;
        case '항상':
          score = 0;
          break;
        default:
          score = 0;
      }
    } else {
      switch (value) {
        case '아니다':
          score = 0;
          break;
        case '가끔':
          score = 1;
          break;
        case '자주':
          score = 2;
          break;
        case '항상':
          score = 3;
          break;
        default:
          score = 0;
      }
    }

    setAnswer({ ...answer, [questionId]: score });
  };



  return (
    <div>
      <h1>스트레스 자가 진단</h1>
      <br />
      {curPage.map(function (test) {
        const questionId = `question_${test.id}`;
        return (
          <div key={questionId}>
            <h4 >
              {test.id}. {test.text}
            </h4>
            <br />
            <h4>
              <Form>
                {['전혀', '가끔', '자주', '항상'].map((value) => (
                  <label key={value} style={{ marginRight: '10px' }}>
                    <Form.Check
                      // className="radi"
                      inline
                      name={questionId}
                      type="radio"
                      value={value}
                      onChange={() => change(questionId, value)}
                      checked={answer[questionId] === value}
                    />
                    {value}
                  </label>
                ))}
              </Form>
            </h4>
            <br />
          </div>
        );
      })}

 {isFristPage ? (
    <button onClick={()=>{setPage(page-1)}}>이전</button>
 ) : null
};

      {isLastPage ? (
        <button onClick={nextPage}>결과보기</button>
      ) : (
        <button onClick={nextPage}>다음</button>
      )}

        {/* 결과 페이지 */}
      {/* <Routes>
        <Route path='/result' element={<Result answers={answer} />} />
      </Routes> */}
      
    </div>
  );
}

export default Main;
