export function calculateScores() {
  const spec = [4, 5, 7, 8];
  const answers = Main.setAnswer; // Main.setAnswer를 어딘가에서 가져온다고 가정
  const scores = [];

  // 모든 질문에 대한 점수를 계산하여 배열에 저장
  for (const questionId in answers) {
      const value = answers[questionId];
      let score = 0;

      if (spec.includes(questionId)) {
          switch (value) {
              case '전혀':
                  score = 3;
                  break;
              case '가끔':
                  score = 2;
                  break;
              case '자주':
                  score = 1;
                  break;
              case '항상':
                  score = 0;
                  break;
              default:
                  score = 0;
          }
      } else {
          switch (value) {
              case '전혀':
                  score = 0;
                  break;
              case '가끔':
                  score = 1;
                  break;
              case '자주':
                  score = 2;
                  break;
              case '항상':
                  score = 3;
                  break;
              default:
                  score = 0;
          }
      }

      scores.push(score);
  }

  return scores;
}
