import React, { useState, useEffect } from 'react';
import Main from './Main';
import { useLocation } from 'react-router-dom';

function Result(props) {
  const location = useLocation();
  const answer = location.state;
  console.log(answer);

  // 결과를 계산할 때 사용할 상태 변수
  const [totalScore, setTotalScore] = useState(0);

  useEffect(() => {
    let score = 0;

    // answer.value를 기준으로 점수 계산
    switch (answer.value) {
      case '전혀':
        score = 3;
        break;
      case '가끔':
        score = 2;
        break;
      case '자주':
        score = 1;
        break;
      case '항상':
        score = 0;
        break;
      default:
        score = 0;
    }

    // totalScore 상태 업데이트 (누적)
    setTotalScore(totalScore + score); // 직접 상태를 업데이트
  }, [answer]);

  console.log(totalScore);

  return (
    <div>
      {/* 여기에서 결과를 표시하는 UI를 렌더링할 수 있습니다. */}
      <p>Total Score: {totalScore}</p>
    </div>
  );
}

export default Result;
