import React, { useState } from 'react';

function Result( {value}) {
  // 결과를 계산할 때 사용할 상태 변수
  const [totalScore, setTotalScore] = useState(0);
  
  // 사용자의 답변을 기반으로 결과를 계산하는 함수
  let calculateScore = (questionId, value) => {
    const spec = [4, 5, 7, 8];
    let score;

    if (spec.includes(questionId)) {
      switch (value) {
        case '전혀':
          score = 3;
          break;
        case '가끔':
          score = 2;
          break;
        case '자주':
          score = 1;
          break;
        case '항상':
          score = 0;
          break;
        default:
          score = 0;
      }
    } else {
      switch (value) {
        case '전혀':
          score = 0;
          break;
        case '가끔':
          score = 1;
          break;
        case '자주':
          score = 2;
          break;
        case '항상':
          score = 3;
          break;
        default:
          score = 0;
      }
    }
    
    setTotalScore+= score;
   
  };


  return (
    <div>
      <h1>결과</h1>
      <p> 총 점수 : {setTotalScore}</p>
    </div>
  );
}

export default Result;
