import React, { useState, useEffect } from 'react';
import {Navigate, useLocation, useNavigate} from 'react-router-dom';
import axios from "axios";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import Board from './Board';
import {Link} from "react-router-dom";
import BoardDetail from './BoardDetail';

  function List(props) {
    var [data, setData] = useState([]);
    let navigate = useNavigate();
    let location = useLocation();
    const today = new Date;
    const date = `${today.getFullYear()}년 ${today.getMonth() + 1}월 ${today.getDate()}일`;


    useEffect(() => {
      axios.get('http://localhost:4000/Board')
      .then((response) => {
        setData(response.data); // 서버에서 받은 데이터를 상태 변수에 저장
        //console.log(response.data);
      })
      .catch((error) => {
        console.error('데이터 가져오기 중 오류 발생:', error);
      });
    }, []); 
 
  

  return (
    <div>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>번호</th>
            <th>제목</th>
            {/* <th>내용</th> */}
            <th>작성자</th>
            <th>작성일자</th>
          </tr>
        </thead>
        <tbody>
    
      {
      Object.keys(data).map((a, i) => (
      <tr key={i}>
      <td>{i +1 }</td>
      <td>
      {/* <Link to={`/boardDetail/${data[a].no}`}>{data[a].BOARD_TITLE}</Link> */}
      <Link to={`/Board/${i + 1}`}>{data[a].BOARD_TITLE}</Link>
      </td>
      {/* <td>{data[a].BOARD_CONTENT}</td> */}
      <td>{data[a].BOARD_NAME}</td>
      <td>{data}</td>
      </tr>
       ))}

        </tbody>
      </Table>
      <Button variant="info">글쓰기</Button>
      <Button variant="secondary">수정하기</Button>
      <Button variant="danger">삭제하기</Button>
    </div>
  );
}

export default List;
