import React from 'react';

function Result( {answers}) {
  // 결과를 계산할 때 사용할 상태 변수
  const totalScore = 0;

  // 사용자의 답변을 기반으로 결과를 계산하는 함수
  const calculateScore = (questionId, value) => {
    const spec = [4, 5, 7, 8];
    let score;

    if (spec.includes(questionId)) {
      switch (value) {
        case '전혀':
          score = 3;
          break;
        case '가끔':
          score = 2;
          break;
        case '자주':
          score = 1;
          break;
        case '항상':
          score = 0;
          break;
        // default:
        //   score = 0;
      }
    } else {
      switch (value) {
        case '전혀':
          score = 0;
          break;
        case '가끔':
          score = 1;
          break;
        case '자주':
          score = 2;
          break;
        case '항상':
          score = 3;
          break;
        // default:
        //   score = 0;
      }
    }

    totalScore += score;
   
  };

  // 답변 데이터를 기반으로 점수 계산
  for (const key in answers) {
    if (answers.hasOwnProperty(key)) {
      calculateScore(parseInt(key), answers[key]);
    }
  }

  return (
    <div>
      <h1>결과</h1>
      <p>총 점수: {totalScore}</p>
    </div>
  );
}

export default Result;
