import React, { useState } from 'react';

function Result(props) {
  // 결과를 계산할 때 사용할 상태 변수
  const [totalScore, setTotalScore] = useState(0);

  const spec = [4, 5, 7, 8];
  let score;

  if (spec.includes(props.questionId)) {
    switch (props.value) {
      case '전혀':
        score = 3;
        break;
      case '가끔':
        score = 2;
        break;
      case '자주':
        score = 1;
        break;
      case '항상':
        score = 0;
        break;
      default:
        score = 0;
    }
  } else {
    switch (props.value) {
      case '전혀':
        score = 0;
        break;
      case '가끔':
        score = 1;
        break;
      case '자주':
        score = 2;
        break;
      case '항상':
        score = 3;
        break;
      default:
        score = 0;
    }
  }

  // totalScore 상태를 업데이트
  setTotalScore(totalScore + score);

  return (
    <div>
      <h1>결과</h1>
      <div> 총 점수 : {totalScore}</div>
    </div>
  );
}

export default Result;
