import React, { useState, useEffect } from 'react';
import {useLocation} from 'react-router-dom';
import Axios from "axios";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";


function List(props) {
  
  const location = useLocation();
  const newPost = location.state.newPost;
  const title = location.state.title;
  const who = location.state.who;
  const content = location.state.content;

  useEffect(() => {
    // 서버에서 데이터를 가져와서 newPost 배열에 저장합니다.
    Axios.post("http://localhost:4000/")
      .then((response) => {
        const fetchedData = response.data; // 서버에서 가져온 데이터
        setNewPost(fetchedData); // 데이터를 newPost 상태에 설정
      })
      .catch((error) => {
        console.error("데이터 가져오기 오류:", error);
      });
  }, []); // 빈 배열을 두 번째 인수로 전달하여 한 번만 실행되도록 설정합니다.

  return (
    <div>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>선택</th>
            <th>번호</th>
            <th>제목</th>
            <th>작성자</th>
            <th>내용</th>
          </tr>
        </thead>
        <tbody>
          {newPost.map((item, key) => (
            <tr key={key}>
              <td>
                <input type="checkbox"></input>
              </td>
              <td>{item.BOARD_NUMBER}</td>
              <td>{item.TITLE}</td>
              <td>{item.WRITER}</td>
              <td>{item.CONTENT}</td>
            </tr>
          ))}
        </tbody>
      </Table>
      <Button variant="info">글쓰기</Button>
      <Button variant="secondary">수정하기</Button>
      <Button variant="danger">삭제하기</Button>
    </div>
  );
}



export default List;
