import { useEffect, useState } from "react";
import Form from 'react-bootstrap/Form';
import data from "../data.js";
import { Routes, Route, Link, useNavigate } from 'react-router-dom';




function Main() {
  let [answer, setAnswer] = useState({});
  let [num, setNum] = useState([]);
  let [page, setPage] = useState(1);
  let totalPage = 5; // 한 페이지에 표시할 질문의 수를 나타내는 변수
  
  let start = (page - 1) * totalPage;
  let end = start + totalPage;
  let curPage = data.slice(start, end);
  const isLastPage = end >= data.length;
  const isFristPage = start != 0 ;
  let navigate = useNavigate();

  

  const nextPage = () => {
    let ques = data
      .slice(start, end)
      .filter((test) => !answer[`question_${test.id}`]);

    if (ques.length > 0) {
      const num = ques.map((question) => question.id);
      setNum(num);
      alert(`${num.join(", ")} 번 질문에 답변해주세요!`);
    } else {
      if (isLastPage) {
        navigate('/result');
      } else {
        setPage(page + 1);
      }
    }
  };

  
 
  return (
    <div>
      <h1>스트레스 자가 진단</h1>
      <br />
      {curPage.map(function (test) {
        const questionId = `question_${test.id}`;
        return (
          <div key={questionId}>
            <h4 >
              {test.id}. {test.text}
            </h4>
            <br />
            <h4>
              <Form>
                {['전혀', '가끔', '거의', '자주', '매우자주'].map((value) => (
                  <div className="mb-3" key={value}>
                    <Form.Check
                      className="radi"
                      inline
                      name={questionId}
                      type="radio"
                      value={value}
                      onChange={() => handleRadioChange(questionId, value)}
                      checked={answers[questionId] === value}
                    />
                    {value}
                  </div>
                ))}
              </Form>
            </h4>
            <br />
          </div>
        );
      })}

 {isFristPage ? (
    <button onClick={()=>{setPage(page-1)}}>이전</button>
 ) : null
};

      {isLastPage ? (
        <button onClick={nextPage}>결과보기</button>
      ) : (
        <button onClick={nextPage}>다음</button>
      )}


    </div>
  );
}

export default Main;
