import { useEffect, useState } from "react";
import Form from 'react-bootstrap/Form';
import data from "../data.js";
import { Routes, Route, Link, useNavigate } from 'react-router-dom';

function Main() {
  const [answers, setAnswers] = useState({}); // 라디오 버튼 상태를 저장하는 상태 변수
  const [page, setPage] = useState(1);
  const totalPage = 5;
  const start = (page - 1) * totalPage;
  const end = start + totalPage;
  const curPage = data.slice(start, end);
  const isLastPage = end >= data.length;
  const isFirstPage = start === 0;
  const navigate = useNavigate();

  // 페이지가 변경될 때 이전 페이지의 라디오 버튼 상태를 저장하고, 현재 페이지의 상태를 불러옴
  useEffect(() => {
    const currentPageAnswers = {};
    curPage.forEach((test) => {
      const questionId = `question_${test.id}`;
      currentPageAnswers[questionId] = answers[questionId] || "";
    });
    setAnswers((prevAnswers) => ({ ...prevAnswers, ...currentPageAnswers }));
  }, [page]);

  const nextPage = () => {
    const unansweredQuestions = data.slice(start, end).filter((test) => !answers[`question_${test.id}`]);

    if (unansweredQuestions.length > 0) {
      const num = unansweredQuestions.map((question) => question.id);
      alert(`${num.join(", ")} 번 질문에 답변해주세요!`);
    } else {
      if (isLastPage) {
        navigate('/result');
      } else {
        setPage(page + 1);
      }
    }
  };

  const handlePreviousPage = () => {
    if (!isFirstPage) {
      setPage(page - 1);
    }
  };

  const handleRadioChange = (questionId, value) => {
    setAnswers({
      ...answers,
      [questionId]: value
    });
  };

  return (
    <div>
      <h1>스트레스 자가 진단</h1>
      <br />
      {curPage.map(function (test) {
        const questionId = `question_${test.id}`;
        return (
          <div key={questionId}>
            <h4 >
              {test.id}. {test.text}
            </h4>
            <br />
            <h4>
              <Form>
                {['전혀', '가끔', '거의', '자주', '매우자주'].map((value) => (
                  <div className="mb-3" key={value}>
                    <Form.Check
                      className="radi"
                      inline
                      name={questionId}
                      type="radio"
                      value={value}
                      onChange={() => handleRadioChange(questionId, value)}
                      checked={answers[questionId] === value}
                    />
                    {value}
                  </div>
                ))}
              </Form>
            </h4>
            <br />
          </div>
        );
      })}

      {isFirstPage ? null : (
        <button onClick={handlePreviousPage}>이전</button>
      )}

      {isLastPage ? (
        <button onClick={nextPage}>결과보기</button>
      ) : (
        <button onClick={nextPage}>다음</button>
      )}
    </div>
  );
}

export default Main;
