import { useState} from "react";
import Form from 'react-bootstrap/Form';
import data from "../data.js";


function Main (){
  
  let [answer, setAnswer] = useState({});
  let [showError, setError] = useState(false);
  let [num, setNum] = useState([]);

  const change = (questionId, value) => {
    setAnswer({
      ...answer,
      [questionId]: value
    });
  };

  const handleNextButtonClick = () => {
    const unansweredQuestions = data.filter(test => !answer[`question_${test.id}`]);

    if (unansweredQuestions.length > 0) {
      setShowErrorMessage(true);
      const unansweredNumbers = unansweredQuestions.map(question => question.id);
      setUnansweredQuestionNumbers(unansweredNumbers);
      return;
    }

    setShowErrorMessage(false);
    // 모든 질문에 답변한 후에 다음 단계로 넘어갈 로직을 추가하세요.
  };


  return(
    <div>
    <h1>스트레스 자가 진단</h1>
    <br/>
    {
      data.map(function(test){
      const questionId = `question_${test.id}`;
        return(
            <div  key={questionId}> 
            <h4 style={{fontWeight : "600"}}>{test.id}. {test.text}</h4>
            <br/>
            <h4 >
            <Form>
      {[ 'radio'].map((type) => (
        <div className="mb-3" key={type}>
          <Form.Check 
            inline
            name={questionId}
            type={type}
            onChange={(e) => {
              setAnswer({
                ...answer,
                [questionId]: e.target.value
              });
            }}
          />전혀
          <Form.Check
            inline
            name={questionId}
            type={type}
            onChange={(e) => {
              setAnswer({
                ...answer,
                [questionId]: e.target.value
              });
            }}
          />가끔
          <Form.Check
            inline
            type={type}
            name={questionId}
            onChange={(e) => {
              setAnswer({
                ...answer,
                [questionId]: e.target.value
              });
            }}
          />거의
            <Form.Check
            inline
            type={type}
            name={questionId}
            onChange={(e) => {
              setAnswer({
                ...answer,
                [questionId]: e.target.value
              });
            }}
          />자주
          <Form.Check
            inline
            type={type}
            name={questionId}
            onChange={(e) => {
              setAnswer({
                ...answer,
                [questionId]: e.target.value
              });
            }}
          />매우자주
        </div>
      ))}
    </Form>
          </h4>
          <br/>
          </div>
        )
        
      })}
      <button onClick={()=>{
        const Questions= data.filter((test) => !answer[`question_${test.id}`]);
        if (Questions.length > 0) {
            // alert('모든 질문에 답변해주세요!');
            setError(true);
            const num = unansweredQuestions.map(question => question.id);
            setNum(num);
            return;

            } 
      }


      // const handleNextButtonClick = () => {
      //   const unansweredQuestions = data.filter(test => !answer[`question_${test.id}`]);
    
      //   if (unansweredQuestions.length > 0) {
      //     setShowErrorMessage(true);
      //     const unansweredNumbers = unansweredQuestions.map(question => question.id);
      //     setUnansweredQuestionNumbers(unansweredNumbers);
      //     return;
      //   }
    }>다음</button>

    </div>
    )
}

export default Main;
