import React, { useEffect, useState } from 'react';
import { calculateScore } from './Calculate'; // calculateScore 함수를 가져옵니다.

function Result({ userAnswers }) {
  const [totalScore, setTotalScore] = useState(0);

  // 사용자 답변이 업데이트될 때마다 총 점수를 계산합니다.
  useEffect(() => {
    // reduce를 사용하여 총 점수를 계산합니다.
    const score = userAnswers.reduce((accumulator, { questionId, value }) => {
      const questionScore = calculateScore(questionId, value);
      return accumulator + questionScore;
    }, 0);

    // 총 점수를 설정합니다.
    setTotalScore(score);
  }, [userAnswers]); // userAnswers 배열이 업데이트될 때만 실행됩니다.

  return (
    <div>
      <h1>결과</h1>
      <div> 총 점수 : {totalScore} </div>
    </div>
  );
}

export default Result;
