import { useState} from "react";
import Form from 'react-bootstrap/Form';
import data from "../data.js";


function Main (){
  
  let [answer, setAnswer] = useState({});
  const questionId = `question_$(test.id)`;



    return(
    <div>
    <h1>스트레스 자가 진단</h1>
    <br/>
    {
    data.map(function(test){
        return(
            <div style={{whiteSpace : 'pre-wrap'}} key={questionId}> 
            <h4 style={{fontWeight : "600"}}>{test.id}. {test.text}</h4>
            <br/>
            <h4 >
            <Form>
      {[ 'radio'].map((type) => (
        <div className="mb-3" key={type}>
          <Form.Check 
            inline
            name={questionId}
            type={type}
            onChange={(e) => {
              // 2. 각 질문에 대한 답변을 업데이트할 때 setAnswer 사용
              setAnswer({
                ...answer,
                [questionId]: e.target.value
              });
            }}
          />전혀
          <Form.Check
            inline
            name={questionId}
            type={type}

          />가끔
          <Form.Check
            inline
            type={type}
            name={questionId}
          />거의
            <Form.Check
            inline
            type={type}
            name={questionId}
          />자주
          <Form.Check
            inline
            type={type}
            name={questionId}
          />매우자주
        </div>
      ))}
    </Form>
          </h4>
          <br/>

          </div>
        )
        
      })}
      <button onClick={()=>{
        const Questions= data.filter((test) => !answer[`question_${test.id}`]);
        if (Questions.length === data.length) {
            alert('모든 질문에 답변해주세요!');
            } 
      }
    }>다음</button>

    </div>
    )
}

export default Main;
