import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Link } from 'react-router-dom';

function Board() {


  // 게시글 제목, 글쓴이, 내용, 그리고 선택한 파일을 관리할 상태 변수
  const [title, setTitle] = useState('');
  const [who, setWho] = useState('');
  const [content, setContent] = useState('');
  const [file, setFile] = useState('');
  let navigate = useNavigate();
  
  // 게시글을 제출하는 함수
  const Submit = () => {
    // 여기에서 게시글을 서버에 보내거나 다른 작업을 수행할 수 있습니다.
    // 선택한 파일 정보도 이용하여 업로드할 수 있습니다.
    // console.log('제목:', title);
    // console.log('글쓴이:', who);
    // console.log('내용:', content);
    // console.log('선택한 파일:', file);

    axios.post("http://localhost:3000", {}).then(() => {
      alert("등록 완료!");
  });


   const newPost = [
      title,
      who,
      content,
   ];
    // console.log(newPost);
    //addPost(newPost);
   
    navigate('/List',  {
      state : {
      title ,
      who
    }
  });

  // axios.get("http://localhost:4000/", newPost)
  // .then((response) => {
  //   console.log(response.data); // 서버에서 받은 응답 데이터를 확인합니다.
  //   alert("등록 완료!");
  // })
  // .catch((error) => {
  //   console.error("등록 중 오류 발생:", error);
  // });

  // useEffect(() => {
  //   axios.post('http://localhost:4000/').then((response)=> {
  //     setCategory(response.data);
  //   })
  // }, []);
 
  

    // 게시글 작성 후 입력 내용 초기화
    setTitle('');
    setWho('');
    setContent('');
    setFile('');
  };

 
  
  return (
    
    <div className='bar'>
      <h1>글 작성</h1>
      <form>
        <div>
          <div htmlFor="title">제목</div>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className='space'
          />
        </div>
        <div>
          <div htmlFor="who">글쓴이</div>
          <input
            type="text"
            id="who"
            value={who}
            onChange={(e) => setWho(e.target.value)}
            className='space'
          />
        </div>
        <div>
          <div htmlFor="content">내용</div>
          <textarea
            id="content"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            className='space1'
          />
        </div>
        <div>
          <input
            type="file"
            id="file"
            accept="image/*" // 이미지 파일만 허용하도록 설정
            value={file}
            onChange={(e) => setFile(e.target.value)}
            className='space'
          />
        </div>
        <button type="button" onClick={Submit}>
          게시글 작성
        </button>
      </form>
      
    </div>
  );
}



export default Board;
