import React, { useState, useEffect } from 'react';
import {useLocation} from 'react-router-dom';
import Axios from "axios";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";



function List() {
  
  const location = useLocation();
  const [boardList, setBoardList] = useState([]);
  // const title = location.state.title;
  // const who = location.state.who;
  // const content = location.state.content;
  const { title, who, content } = location.state;
  const Board = ({
    id,
    title,
    registerId,
    registerDate,
}) => {
    return (
        <tr>
            <td>
                <input type="checkbox"></input>
            </td>
            <td>{id}</td>
            <td>{title}</td>
            <td>{registerId}</td>
            <td>{registerDate}</td>
        </tr>
    );
};

const getList = () => {
  Axios.get('http://localhost:3000/list')
    .then((res) => {
      const { data } = res;
      setBoardList(data);
    })
    .catch((e) => {
      console.error(e);
    });
};


  // useEffect(() => {
  //   // 컴포넌트가 처음 마운트될 때 API에서 데이터를 가져옵니다.
  //   Axios.get('http://localhost:3000/list')
  //     .then((res) => {
  //       const { data } = res;
  //       setBoardList(data);
  //     })
  //     .catch((e) => {
  //       console.error(e);
  //     });
  // }, []); // 빈 배열 []은 이 효과가 한 번만 실행되도록 합니다.

  return (
    <div>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>선택</th>
            <th>번호</th>
            <th>제목</th>
            <th>작성자</th>
            <th>내용</th>
          </tr>
        </thead>
        <tbody>
          {boardList.map((item, key) => (
            <tr key={key}>
              <td>
                <input type="checkbox"></input>
              </td>
              <td>{item.BOARD_NUMBER}</td>
              <td>{item.TITLE}</td>
              <td>{item.WRITER}</td>
              <td>{item.CONTENT}</td>
            </tr>
          ))}
        </tbody>
      </Table>
      <Button variant="info">글쓰기</Button>
      <Button variant="secondary">수정하기</Button>
      <Button variant="danger">삭제하기</Button>
    </div>
  );
}

export default List;
