import {React, useState} from 'react';
import Board from './Board';

function List({newPost}) {
    //  const [newPost] = useState(null); // 새로운 게시글 상태
    
    // 새로운 게시글이 있을 때만 출력
    if (newPost) {
      return (
        <div>
          <h2>게시글</h2>
          <ul>
            <li>
              <strong>제목:</strong> {newPost.title}, <strong>글쓴이:</strong> {newPost.who}
            </li>
          </ul>
        </div>
      );
    } else {
      return <div>게시글이 없습니다.</div>;
    }
}

export default List;
