import React from 'react';


function Result({ answers }) {
  // 사용자의 답변을 기반으로 결과를 계산하는 로직을 추가
  let totalScore = (questionId, value) => {
    // 문항 4, 5, 7, 8에 대한 규칙
    const spec = [4, 5, 7, 8];

    // 선택한 값에 따라 점수를 설정
    let score;
    if (spec.includes(questionId)) {
      switch (value) {
        case '전혀':
          score = 3;
          break;
        case '가끔':
          score = 2;
          break;
        case '자주':
          score = 1;
          break;
        case '항상':
          score = 0;
          break;
        default:
          score = 0;
      }
    } else {
      switch (value) {
        case '전혀':
          score = 0;
          break;
        case '가끔':
          score = 1;
          break;
        case '자주':
          score = 2;
          break;
        case '항상':
          score = 3;
          break;
        default:
          score = 0;
      }
    }

    totalScore += answers[score];
    
  };

//   for (const key in answers) {
//     totalScore += answers[key];
//   }

  return (
    <div>
      <h1>결과</h1>
      <p>총 점수: {totalScore}</p>
    </div>
  );
}

export default Result;
