import { useState} from "react";
import Form from 'react-bootstrap/Form';
import data from "../data.js";
import { Routes, Route, Link, useNavigate } from 'react-router-dom';

function Main (){
  
  let [answer, setAnswer] = useState({});
  let [num, setNum] = useState([]);
  let navigate = useNavigate();
  let [page, setPage] = useState(1);
  const quesPage = 5;
  //한 페이지에 표시할 질문의 수를 나타내는 변수

  const totalPage = data.slice(
    (page - 1) * quesPage,
    page * quesPage
  );

  const optChange = (questionId, value) => {
    setAnswer({
      ...answer,
      [questionId]: value
    });
  };



  return(
    <div>
    <h1>스트레스 자가 진단</h1>
    <br/>
    {
      data.map(function(test){
      const questionId = `question_${test.id}`;
        return(
            <div  key={questionId}> 
            <h4 style={{fontWeight : "600"}}>{test.id}. {test.text}</h4>
            <br/>
            <h4 >
            <Form>
      {[ 'radio'].map((type) => (
        <div className="mb-3" key={type}>
          <Form.Check 
            inline
            name={questionId}
            type={type}
            onChange={(e) => {
              setAnswer({
                ...answer,
                [questionId]: e.target.value
              });
            }}
          />전혀
          <Form.Check
            inline
            name={questionId}
            type={type}
            onChange={(e) => {
              setAnswer({
                ...answer,
                [questionId]: e.target.value
              });
            }}
          />가끔
          <Form.Check
            inline
            type={type}
            name={questionId}
            onChange={(e) => {
              setAnswer({
                ...answer,
                [questionId]: e.target.value
              });
            }}
          />거의
            <Form.Check
            inline
            type={type}
            name={questionId}
            onChange={(e) => {
              setAnswer({
                ...answer,
                [questionId]: e.target.value
              });
            }}
          />자주
          <Form.Check
            inline
            type={type}
            name={questionId}
            onChange={(e) => {
              setAnswer({
                ...answer,
                [questionId]: e.target.value
              });
            }}
          />매우자주
        </div>
      ))}
    </Form>
          </h4>
          <br/>
          </div>
        )
        
      })}
      <button onClick={()=>{
        const question= data.filter((test) => !answer[`question_${test.id}`]);
        if (question.length > 0) {
          const num = question.map(question => question.id);
          setNum(num);
          alert(`${num} 번 질문에 답변해주세요!`);
          return;
            } else {
              setPage(page + 1);
              navigate(`/Main${page + 1}`);
              <Routes>
              <Route path="/Main2" element={<Main/>} />
               </Routes>
            }
      }

    }>다음</button>

    </div>
    )
}

export default Main;
