    //     setPost(response.data); // 서버에서 받은 데이터를 상태 변수에 저장
    //     console.log(setPost);
    //     })
    //     .catch((error) => {
    //       console.error('이미지 가져오기 중 오류 발생:', error);
    //     });
    //   }, []); 

    const getBoard = async () => {
      const resp = await (await axios.get(`//localhost:8080/board/${id}`)).data;
      setBoard(resp.data);
      setLoading(false);
    };

useEffect(() => {
    getBoard();
  }, []);


      return (
        <div>
        <h2>게시글 상세정보</h2> <br/>
        
        <div className="post-view-row">
        <label>게시글 번호</label>
        <label>{ post.no }</label>
        </div>
        <div className="post-view-row">
        <label>제목</label>
        <label>{ post.title }</label>
        </div>
        <div className="post-view-row">
        <label>작성자</label>
        <label>{ post.who }</label>
        </div>
        <div className="post-view-row">
        <label>조회수</label>
        <label>{ pcount }</label>
        </div>
       
        <div >
        <td/>
         <div className='content'>
       {
        post.content
         }
         </div>

        </div>
        </div>
      );

}

