import React, { useState , useEffect} from 'react';
import Main from './Main';
import {useLocation} from 'react-router-dom';

function Result (props)  {

  const location = useLocation();

  const answer = location.state;
  console.log(answer);

 //결과를 계산할 때 사용할 상태 변수
  let [score, totalScore] = useState(0);
  
  const spec = [4, 5, 7, 8];

  useEffect(()=>{

    if (spec.includes(answer)) {
      switch (answer) {
        case '전혀':
          score = 3;
          break;
        case '가끔':
          score = 2;
          break;
        case '자주':
          score = 1;
          break;
        case '항상':
          score = 0;
          break;
        default:
          score = 0;
      }
    } else {
      switch (answer) {
        case '전혀':
          score = 0;
          break;
        case '가끔':
          score = 1;
          break;
        case '자주':
          score = 2;
          break;
        case '항상':
          score = 3;
          break;
        default:
          score = 0;
      }
    }

    totalScore((prevTotalScore) => prevTotalScore + score);
  }, [answer]);
  console.log(totalScore);

  }

  



    
  
    
  

export default Result;
