import React, { useState } from 'react';

function Board() {
  // 게시글 제목과 내용을 관리할 상태 변수
  const [title, setTitle] = useState('');
  const [who, setWho] = useState('');
  const [content, setContent] = useState('');
  
  // 게시글을 제출하는 함수
  const submit = () => {
    // 여기에서 게시글을 서버에 보내거나 다른 작업을 수행할 수 있습니다.
    // 예를 들어, 게시글을 API로 전송하는 등의 작업을 수행할 수 있습니다.
    console.log('제목:', title);
    console.log('내용:', content);
    
    // 게시글 작성 후 입력 내용 초기화
    setTitle('');
    setWho('');
    setContent('');
  };

  return (
    <div>
      <h1>글 작성</h1>
      <form>
        <div>
          <label htmlFor="title">제목:</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className='space'
          />
        </div>
        <div>
          <label htmlFor="who">글쓴이:</label>
          <input
            type="text"
            id="who"
            value={who}
            onChange={(e) => setWho(e.target.value)}
          />
        </div>
        <div className='space'/>
        <div>
          <label htmlFor="content">내용:</label>
          <textarea
            id="content"
            value={content}
            onChange={(e) => setContent(e.target.value)}
          />
        </div>
        <div className='space'/>
        <button type="button" onClick={submit}>
          게시글 작성
        </button>
      </form>
    </div>
  );
}

export default Board;
