import { useState } from "react";
import Form from 'react-bootstrap/Form';
import data from "../data.js";
import { Routes, Route, Link, useNavigate } from 'react-router-dom';

function Main() {
  let [answer, setAnswer] = useState({});
  let [num, setNum] = useState([]);
  let [page, setPage] = useState(1);
  let totalPage = 5; // 한 페이지에 표시할 질문의 수를 나타내는 변수
  
  let start = (page - 1) * totalPage;
  let end = start + totalPage;
  let curPage = data.slice(start, end);
  const isLastPage = end >= data.length;
  const allQues = curPage.every(test => answer[`question_${test.id}`]);

  const nextPage = () => {
    let ques = data
      .slice(start, end)
      .filter((test) => !answer[`question_${test.id}`]);

    if (ques.length > 0) {
      const num = ques.map((question) => question.id);
      setNum(num);
      alert(`${num.join(", ")} 번 질문에 답변해주세요!`);
    } else {
      setPage(page + 1);
    }
  };

  let NextButton = isLastPage ? (
    ques == 0 ? (
    <button>결과보기</button>
  ) : (
    <button disabled>결과보기</button>
  )
  ) : (  
    <button onClick={nextPage}>다음</button>
  );

  return (
    <div>
      <h1>스트레스 자가 진단</h1>
      <br />
      {curPage.map(function (test) {
        const questionId = `question_${test.id}`;
        return (
          <div key={questionId}>
            <h4 style={{ fontWeight: "600" }}>
              {test.id}. {test.text}
            </h4>
            <br />
            <h4>
              <Form>
                {['radio'].map((type) => (
                  <div className="mb-3" key={type}>
                    <Form.Check
                      inline
                      name={questionId}
                      type={type}
                      onChange={(e) => {
                        setAnswer({
                          ...answer,
                          [questionId]: e.target.value
                        });
                      }}
                    />
                    전혀
                    <Form.Check
                      inline
                      name={questionId}
                      type={type}
                      onChange={(e) => {
                        setAnswer({
                          ...answer,
                          [questionId]: e.target.value
                        });
                      }}
                    />
                    가끔
                    <Form.Check
                      inline
                      type={type}
                      name={questionId}
                      onChange={(e) => {
                        setAnswer({
                          ...answer,
                          [questionId]: e.target.value
                        });
                      }}
                    />
                    거의
                    <Form.Check
                      inline
                      type={type}
                      name={questionId}
                      onChange={(e) => {
                        setAnswer({
                          ...answer,
                          [questionId]: e.target.value
                        });
                      }}
                    />
                    자주
                    <Form.Check
                      inline
                      type={type}
                      name={questionId}
                      onChange={(e) => {
                        setAnswer({
                          ...answer,
                          [questionId]: e.target.value
                        });
                      }}
                    />
                    매우자주
                  </div>
                ))}
              </Form>
            </h4>
            <br />
          </div>
        );
      })} 
     {NextButton}
      
    </div>
  );
}

export default Main;
