import React from 'react';
import { Link } from 'react-router-dom';
import { useNavigate , useLocation , useParams} from 'react-router-dom';
import axios from "axios";
import { useState } from 'react';
import { useEffect } from 'react';
import Board from './Board';

function BoardDetail() {
   
    let location = useLocation();
    const [post, setPost] = useState(null); // 게시글 정보를 저장할 상태 변수
    const { newPost } = location.state;
    const {postId } = location.state;
    const [detail, setDetail] = useState('');
    let [pcount , tcount] = useState(0);
    const { id } = useParams();

    const [board, setBoard] = useState({});
    const [loading, setLoading] = useState(true);
    
    // useEffect(() => {
    //   axios.get(`http://localhost:4000/BoardDetail/${id}`)
    //     .then((response) => {
    //     setPost(response.data); // 서버에서 받은 데이터를 상태 변수에 저장
    //     console.log(setPost);
    //     })
    //     .catch((error) => {
    //       console.error('이미지 가져오기 중 오류 발생:', error);
    //     });
    //   }, []); 

    const getBoard = async () => {
      const resp = await (await axios.get(`//localhost:8080/board/${id}`)).data;
      setBoard(resp.data);
      setLoading(false);
    };

    useEffect(() => {
      getBoard();
    }, []);
  

    return (
      <div>
        {loading ? (
          <h2>loading...</h2>
        ) : (
          <div>
          <div className="post-view-row">
        <label>게시글 번호</label>
        <label>{ board.no }</label>
        </div>
        <div className="post-view-row">
        <label>제목</label>
        <label>{ board.title }</label>
        </div>
        <div className="post-view-row">
        <label>작성자</label>
        <label>{ board.who }</label>
        </div>
        <div className="post-view-row">
        <label>조회수</label>
        <label>{ pcount }</label>
        </div>
       
        <div >
        <td/>
         <div className='content'>
       {
        post.content
         }
         </div>

        </div>
        </div>
        )}
      </div>
    );
  };
  export default BoardDetail;


